mapper.select:
select count( nick )
from user
where nick like #{ nick };

select count( id )
from user
where id like #{ id };

select count(*)
from user
where id like #{id} and pwd like #{pwd}

select pwd
from user
where id like #{id}

select count( id )
from user
where id like #{ id };

select count(*)
from user
where id = #{id} and mail = #{mail}

select pwd
from user
where id = #{id} and mail = #{mail}

select no, subject, writer, date_format(wdate, '%Y-%m-%d') wdate, content, hit, location, video, reply, heart
from l_board
where no=#{no}

select no, subject, content, writer, date_format(wdate, '%Y-%m-%d') wdate, hit, location, picture, reply
from la_board
where no = #{no}

select no, subject, content, writer, wdate, hit, location, picture, reply, heart
from s_board
where no = #{no}

mapper.update:
update s_board
set hit = hit + 1 
where no = #{no}

mapper.select:
select no, bno, writer, content, wdate
from s_reply
where bno = #{bno}

