mapper.select:
select count( id )
from user 
where id like #{ id };


select count(*)
from user 
where id like #{id} and pwd like #{pwd}

select pwd
from user
where id like #{id}

select count( id )
from user 
where id like #{ id };


select count(*)
from user 
where id = #{id} and mail = #{mail}

select pwd
from user
where id = #{id} and mail = #{mail}
